mapper.select:
SELECT * FROM points_transactions WHERE user_id=#{userId}
ORDER BY id DESC
 LIMIT #{offset}, #{size}

SELECT pt.id, pt.user_id as userId, pt.change_amount as changeAmount, pt.balance_after as balanceAfter, pt.type, pt.remark, pt.related_id as relatedId, pt.created_at as createdAt, u.username, u.email FROM points_transactions pt LEFT JOIN users u ON pt.user_id = u.id
AND (u.username LIKE CONCAT('%', #{keyword}, '%') OR u.email LIKE CONCAT('%', #{keyword}, '%'))
AND pt.type = #{type}
ORDER BY pt.id DESC LIMIT #{offset}, #{size}

SELECT COUNT(1) FROM points_transactions pt LEFT JOIN users u ON pt.user_id = u.id
AND (u.username LIKE CONCAT('%', #{keyword}, '%') OR u.email LIKE CONCAT('%', #{keyword}, '%'))
AND pt.type = #{type}
